import React, { Component } from 'react';
import Footer from '../commons/Footer';
import {Link} from 'react-router-dom';
import axios from 'axios';

export class Register extends Component {


    constructor(props) {
        super(props);

        this.state = {

            email: '',
            password: '',
            confirmpassword: '',
            isSubmitDisabled: false,
            isVerifyComplete: 0,
            isLoading: false,

        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
       this.validatePassword = this.validatePassword.bind(this);

      }


      handleSubmit(e) {
        // Form submission logic
        e.preventDefault();

        this.setState({ isLoading: true });

        this.setState({ [e.target.name]: e.target.value });

        const headers = {
          "Content-Type": "application/json",
        }

        const data = { FirstName:this.state.first_name, LastName:this.state.last_name, Email: this.state.email, Password: this.state.password };  

        //console.log(data);

       axios.post(`https://api-business.azurewebsites.net/api/auth/register`, data, {headers: headers})
        .then((response) => {

          //  if (response.data.success === true) 
          //  {
          //     this.setState({
          //       isVerifyComplete: 1,
          //     });

          //     this.props.history.push("/get-started");
          //  }  
          //  else 
          //  {
          //     this.props.history.push('/signup'); 
          //  }  

           this.props.history.push("/register/success");

           //console.log(response.data);
         })
         .catch( error => {
           console.log(error.response);
         });


      }

      handleChange(e) {
        this.setState({ [e.target.name]: e.target.value });
        if(this.state.password.value !== this.state.confirmpassword.value) 
        {
          //confirm_password.setCustomValidity("Passwords Don't Match");
          alert("Passwords Don't Match");

        } 
        else 
        {
          // confirm_password.setCustomValidity('');
        }
      }



      componentDidMount() {

      }



    render() {
        return (
            <div>
            <header className="container-fluid bg-white">
                <nav className="p-2">
                <a className="navbar-brand" href="#">
                    <img src="https://kokoruns.com/public/assets/Images/Header%20and%20Footer/Logo.png" alt="Logo" style={{width: 200}} />
                </a>

                </nav>
            </header>

            <div className="container " style={{margin: '4rem'}}>
                <div className="row bg-white w-75 mx-auto">
                <div className="col-md-7">
                    <div className="pt-4 pl-4 pr-4">
                    <h2 className="mb-3">Create A User Account</h2>

                    <form action="<?php echo site_url()?>front/registeraction" method="POST">
                        <div className="form-group">
                        <input type="text" className="form-control rounded-0" id="user-email-or-phone-number" name="user-email-or-phone-number" placeholder="Enter Email or Phone Number" autoComplete="off" required />
                        </div>
                        <div className="form-group">
                        <input type="password" className="form-control rounded-0" placeholder="Enter password" id="myPassword" name="password" autoComplete="off" required />
                        </div>
                        <div className="form-group">
                        <input type="password" className="form-control rounded-0" placeholder="Retype password" id="myConfirmPassword" name="password2" autoComplete="off" required />
                        </div>
                        <div className="form-group">
                        <button type="submit" className="btn btn-block rounded-0 text-white w-50 mx-auto" style={{background: 'red'}} id="submit">Register</button>
                        </div>
                    </form>
                    <h6 className="text-center text-primary">OR</h6>
                    <div className="row">
                        <div className="col">
                        <button type="button" className="btn btn-white btn-block rounded-0 border w-100">
                            <img className="float-left" src="assets/Images/Index/Google%20Logo.png" alt width="5%" /> <span className="text-primary"> Continue with Google</span>
                        </button>
                        <button type="button" className="btn btn-block rounded-0 border w-100" style={{background: '#3C5A99'}}>
                            <img className="float-left" src="assets/Images/Index/Facebook%20Logo.png" alt width="5%" /> <span className="text-white"> Continue with Facebook</span>
                        </button>
                        <button type="button" className="btn btn-dark btn-block rounded-0 border w-100" style={{background: '#000'}}>
                            <img className="float-left" src="data:image/png;base64," alt width="5%" /> <span className="text-white"> Sign Up with Apple</span>
                        </button>
                        </div>
                    </div>
                        <p className="text-center mt-2">Already have an account? 
                            <Link to={"/login"} > Sign in</Link>
                        </p>
                    </div>
                </div>
                <div className="col-md-5" style={{backgroundImage: 'url(assets/Images/Login%20and%20Sign%20up/illustration_register.jpg)', backgroundPosition: 'center', backgroundRepeat: 'no-repeat', backgroundSize: 'cover', height: 504}}>
                    <div className="pl-4 text-white" style={{paddingTop: '8rem'}}>
                        <h4 className="mb-3 "> - Connect With Employers</h4>
                        <h4 className="mb-3">- Join Teams</h4>
                        <h4 className="mb-3">- Showcase Your Portfolio</h4>
                        <h4 className="mb-3"> - Find Users</h4>
                        <h4 className="mb-3"> - Register for Events</h4>
                    </div>
                </div>
                </div>
            </div>


                    <Footer/>


            </div>

        )
    }
}

export default Register
