import React, { Component } from 'react'
import {Link} from 'react-router-dom';
import axios from 'axios';

export class Login extends Component {
    constructor(props) {
        super(props);

        this.state = {

            email: '',
            password: '',
            loggedIn: false,

        }

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);

      }


      handleSubmit(e) {
        // Form submission logic
        e.preventDefault();


        const headers = {
          "Content-Type": "application/json",
        }

        //const form_data = new FormData(e.target);

       // Display the key/value pairs
        // for (var pair of form_data.entries()) {
        //   console.log(pair[0]+ ', ' + pair[1]); 
        // }

        this.setState({ [e.target.name]: e.target.value });

        const data = { Email: this.state.email, Password: this.state.password };  

        //console.log(data);


        axios.post(`https://api-business.azurewebsites.net/api/auth/login`, data, {headers: headers})
        .then( (response) => {

          // if (response.data.Status === 'Invalid') 
          // {
          //   alert('Invalid User');  
          // }  
          // else 
          // {
          //     //history.push('/Dashboard') 
          // }  

          // localStorage.setItem('currentUser', JSON.stringify(response.data));

          // //console.log(response);

          // console.log(this.parseJwt(response.data))

          // const token = jwt(response.data); // decode your token here

          // console.log(token);

          // var decoded = jwt(response.data, 'TkfhULfmpEa8eoU8Ocdocg==3BP1A2POSO1E0KAOUAY58RZ2O7UBWP9Y');
          // console.log(decoded);
          //console.log(response.data.data);

          var decoded = jwt(response.data.data);
          console.log(decoded);

          localStorage.setItem('currentUserId', decoded.nameid);
          localStorage.setItem('currentUserEmail', decoded.given_name);
          localStorage.setItem('currentUserRole', decoded.role);
          localStorage.setItem('full_token', response.data.data);

          this.setState({ 'loggedIn': true});

          console.log(this.state.loggedIn);

          this.props.history.push("/setup");

        })
        .catch( error => {
          console.log(error);
        });





      }

      handleChange(e) {
        this.setState({ [e.target.name]: e.target.value })
      }

      componentDidMount() {

      }

    render() {
        return (
                <>
                <header className="container-fluid bg-white">
                    <nav className="p-2">
                    <a className="navbar-brand" href="<?php echo site_url(); ?>">
                        <img src="https://kokoruns.com/public/assets/Images/Header%20and%20Footer/Logo.png" alt="Logo" style={{width: 200}} />
                    </a>
                    {/* <a href="<?php //echo site_url(); ?>logout" class="float-right pt-4">
                                    <i class="fa fa-power-off" data-toggle="tooltip" title="Logout"></i>
                            </a> */}
                    </nav>
                </header>
                <div className="container mx-auto" style={{margin: '4rem'}}>
                    <div className="row bg-white w-50 mx-auto">
                    <div className="col">
                        <div className="pt-4 pl-4 pr-4">
                        <h2 className="mb-3 text-center">Login Into Your Account</h2>

                        <form action="<?php echo site_url()?>front/loginaction" method="POST">
                            <div className="form-group">
                            <input type="text" className="form-control rounded-0" id="user-email-or-phone-number" name="user-email-or-phone-number" placeholder="Enter Email or Phone Number" autoComplete="off" required />
                            </div>
                            <div className="form-group">
                            <input type="password" className="form-control rounded-0" placeholder="Enter password" id="myPassword" name="password" autoComplete="off" required />
                            </div>
                            <div className="form-group">
                            <button type="submit" className="btn btn-block rounded-0 text-white w-50 mx-auto" style={{background: 'red'}} id="submit">Login</button>
                            </div>
                        </form>
                        <h5 className="text-center text-primary">OR</h5>
                        <div className="row">
                            <div className="col">
                            <button type="button" className="btn btn-white btn-block rounded-0 border w-100">
                                <img className="float-left" src="assets/Images/Index/Google%20Logo.png" alt width="5%" /> <span className="text-primary"> Continue with Google</span>
                            </button>
                            <button type="button" className="btn btn-block rounded-0 border w-100" style={{background: '#3C5A99'}}>
                                <img className="float-left" src="assets/Images/Index/Facebook%20Logo.png" alt width="5%" /> <span className="text-white"> Continue with Facebook</span>
                            </button>
                            <button type="button" className="btn btn-dark btn-block rounded-0 border w-100" style={{background: '#000'}}>
                                <img className="float-left" src="data:image/png;base64," alt width="5%" /> <span className="text-white"> Sign Up with Apple</span>
                            </button>
                            </div>
                        </div>
                            <p className="text-center mt-2 mb-1">Dont have an account? 
                                <Link to={"/register"}>  Register</Link>
                            </p>
                            <p className="text-center"> 
                                <Link to={"/forgot"}>  Forgot Password?</Link>
                            </p>
                        </div>
                    </div>
                    </div>
                </div>      
            </>

        )
    }
}

export default Login
